import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import {Observable} from'rxjs'
import { Job } from './job';

@Injectable({
  providedIn: 'root'
})
export class JobsService {

  private baseURL="http://localhost:8080/jobs";
  constructor(private http:HttpClient) { }

  getJobList(): Observable<Job[]>{
    return this.http.get<Job[]>(`${this.baseURL}`);

  }

  createjob(job:Job):Observable<any>{
    return this.http.post("http://localhost:8080/jobs",job);


  }
  getJobById(id:number):Observable<Job>{
    return this.http.get<Job>(`http://localhost:8080jobs/${id}`);

  }

  updateJob(id:number,job:Job):Observable<Object>{
    return this.http.put(`http://localhost:8080/updatejobs/${id}`, job);
  }

  deleteJob(id: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/${id}`, { responseType: 'text' });


  }
}
